mapper.select:
SELECT * FROM (
	SELECT ROWNUM rnum, tb.* FROM (
		SELECT gNum, gSubject, gPrice, gContent, gStatus, m.userNickName, g.userId,
			 TO_CHAR(g.gRegdate, 'YYYY-MM-DD') gRegdate, gcNum,
			 gHitCount, TO_CHAR(g.gExpdate, 'YYYY-MM-DD') gExpdate
		FROM giftycon g
		JOIN member m ON g.userId = m.userId
		
			
				
			
		 
		ORDER BY gNum DESC  
	) tb WHERE ROWNUM <= #{end}
) WHERE rnum >= #{start}
