mapper.select:
SELECT pickedNum, target_id, TO_CHAR(regDate,'YYYY-MM-DD') regDate
FROM pickedUser
WHERE userId = #{userId}

SELECT blockNum, target_id, TO_CHAR(regDate,'YYYY-MM-DD') regDate
FROM blockedUser
WHERE userId = #{userId}

SELECT keywordNum, keyword, TO_CHAR(regDate,'YYYY-MM-DD') regDate
FROM keyword
WHERE userId = #{userId}

SELECT NVL(COUNT(*),0) FROM pickedUser p JOIN member m ON p.target_id = m.userId
p.userId = #{userId}
AND INSTR(m.userNickName, #{keyword}) > 0

SELECT * FROM (
	SELECT ROWNUM rnum, tb.* FROM (	
		SELECT pickedNum, p.userId, p.target_id, m.userNickName, p.regDate
		FROM pickedUser p
		JOIN member m ON p.target_id = m.userId
		
			p.userId = #{userId}
			
				AND
				INSTR(m.userNickName, #{keyword}) > 0
			
		
		ORDER BY p.regDate DESC
	) tb WHERE ROWNUM <= #{end}
) WHERE rnum >= #{start}

SELECT userId
FROM member
WHERE userNickName = #{userNickName}

SELECT NVL(COUNT(*),0) FROM blockedUser p JOIN member m ON p.target_id = m.userId
p.userId = #{userId}
AND INSTR(m.userNickName, #{keyword}) > 0

SELECT * FROM (
	SELECT ROWNUM rnum, tb.* FROM (	
		SELECT blockNum, p.userId, p.target_id, m.userNickName, p.regDate
		FROM blockedUser p
		JOIN member m ON p.target_id = m.userId
		
			p.userId = #{userId}
			
				AND
				INSTR(m.userNickName, #{keyword}) > 0
			
		
		ORDER BY p.regDate DESC	
	) tb WHERE ROWNUM <= #{end}
) WHERE rnum >= #{start}

SELECT NVL(COUNT(*),0) FROM keyword
userId = #{userId}
AND INSTR(keyword, #{keyword}) > 0

SELECT * FROM (
	SELECT ROWNUM rnum, tb.* FROM (	
		SELECT keywordNum, userId, keyword, regDate
		FROM keyword
		
			userId = #{userId}
			
				AND
				INSTR(keyword, #{keyword}) > 0
			
		
		ORDER BY regDate DESC
	) tb WHERE ROWNUM <= #{end}
) WHERE rnum >= #{start}

SELECT NVL(COUNT(*),0)
FROM blockedUser
WHERE userId = #{userId}

SELECT DISTINCT n.receiveId, userNickName
FROM note n
JOIN member m ON n.receiveId = m.userId

	
		
	
	
		AND m.userId NOT IN (
            SELECT target_ID FROM blockeduser
            WHERE userId = #{userId}
        )
	
	AND
	sendId = #{userId}
	AND (
	    isSendDelete = 'F'
	)

ORDER BY userNickName

mapper.update:
UPDATE note SET noteReadDate = SYSDATE
WHERE
	receiveId = #{meId}
    AND sendId = #{youId}
    AND noteReadDate IS NULL
    AND isSendDelete != 'T'
