mapper.select:
SELECT * FROM (
	SELECT ROWNUM rnum, tb.* FROM (
		SELECT v.vNum, v.userId, content, userNickName, subject,
			   vcNum
		FROM village_forone v
		JOIN member m ON v.userId = m.userId
		
		vcNum = 8
			
				AND 
			
			
		
		ORDER BY vNum DESC
	) tb WHERE ROWNUM <=#{end}
) WHERE rnum >=#{start}

mapper.update:
UPDATE village_forone SET subject=#{subject}, content=#{content}, areaNum=#{areaNum},
	vBlat=#{vBlat, jdbcType=DOUBLE}, vBlon=#{vBlon, jdbcType=DOUBLE}
WHERE vNum = #{vNum}
