mapper.select:
SELECT * FROM ( SELECT ROWNUM rnum, tb.* FROM ( SELECT vNum, v.userId, userNickName, subject, TO_CHAR(v.regDate, 'YYYY-MM-DD') reg_date, hitCount, vcNum, area1, area3 FROM village_bbs v JOIN area a ON v.areaNum = a.areaNum JOIN member m ON v.userId = m.userId
LEFT OUTER JOIN ( SELECT USERID, LISTAGG(TARGET_ID, '|') WITHIN GROUP(ORDER BY USERID) AS BLOCKEDID FROM BLOCKEDUSER GROUP BY USERID )BLOCKED ON BLOCKED.USERID = #{userId}
vcNum = 1 AND CASE WHEN blocked.blockedId is null THEN 1 WHEN not regexp_like(v.userId, blocked.blockedId) THEN 1 ELSE 0 END = 1
AND
AND v.areaNum IN ( SELECT areaNum FROM ( SELECT * FROM ( SELECT areaNum, (6371*acos(ROUND(cos(#{maLat}*3.14159265358979/180)*cos(aLat*3.14159265358979/180)*cos(aLon*3.14159265358979/180 -#{maLon}*3.14159265358979/180)+sin(#{maLat}*3.14159265358979/180)*sin(aLat*3.14159265358979/180),30))) AS distance FROM area ar ) WHERE distance <= 5 ) )
ORDER BY vNum DESC ) tb WHERE ROWNUM <=#{end} ) WHERE rnum >=#{start}

mapper.update:
UPDATE village_bbs SET subject=#{subject}, content=#{content}, areaNum=#{areaNum},
	vBlat=#{vBlat, jdbcType=DOUBLE}, vBlon=#{vBlon, jdbcType=DOUBLE}
WHERE vNum = #{vNum}

mapper.delete:
DELETE FROM vbbsReply
WHERE vreplyNum=#{vreplyNum}

	OR vrAnswer=#{vreplyNum}

mapper.select:
SELECT COUNT(DECODE(replyLike, 1, 1)) likeCount,
	   COUNT(DECODE(replyLike, 0, 1)) disLikeCount
FROM vbbsReplyLike WHERE vreplyNum = #{vreplyNum}
