mapper.select:
SELECT * FROM (
	SELECT ROWNUM rnum, tb.* FROM (
		SELECT vNum, v.userId, userNickName, subject,
			TO_CHAR(v.regDate, 'YYYY-MM-DD') reg_date,
			hitCount, vcNum 
		FROM village_bbs v
		JOIN member m ON v.userId = m.userId
		
		vcNum = 1
			
				
			
		
		ORDER BY vNum DESC
	) tb WHERE ROWNUM <= #{end}
) WHERE rnum >= #{start}
